statement.sql:
SELECT a.agency_id, b.agency_name, SUM(total_spending_amount) as check_amount, SUM(total_spending_amount)/MAX(total.check_amount)*100 as percent_spending FROM aggregateon_mwbe_spending_coa_entities a JOIN ref_agency b ON b.agency_id = a.agency_id JOIN ( SELECT SUM(total_spending_amount) AS check_amount FROM aggregateon_mwbe_spending_coa_entities
) total on 1 = 1
GROUP BY a.agency_id,b.agency_name

SELECT b.expenditure_object_id as expense_category_id, b.original_expenditure_object_name as expense_category_name, SUM(total_spending_amount) as check_amount, SUM(total_spending_amount)/MAX(total.check_amount)*100 as percent_spending FROM aggregateon_mwbe_spending_coa_entities a JOIN ref_expenditure_object b ON b.expenditure_object_id = a.expenditure_object_id JOIN ( SELECT SUM(total_spending_amount) AS check_amount FROM aggregateon_mwbe_spending_coa_entities
) total on 1 = 1
GROUP BY b.expenditure_object_id,b.original_expenditure_object_name
